page_settings.custom_css:
/* Normalize all text rendering in Elementor */
body, p, span, div, .elementor-widget-text-editor {
    line-height: 1.6 !important;
    letter-spacing: 0 !important;
    word-spacing: 0 !important;
}

/* Prevent Elementor preview mode from compressing monospace fonts */
.elementor-edit-mode * {
    font-variant-ligatures: none !important;
}
/* Container for the whole ratings block */
.gg-ratings-block {
  margin-top: 32px;
  border-top: 3px solid var(--gg-brown-dark);
  padding-top: 32px;
  display: flex;
  flex-direction: column;
  gap: 32px;
}

/* One row = bar meta + explanation */
.gg-rating-row {
  display: grid;
  grid-template-columns: minmax(220px, 320px) minmax(0, 1fr);
  column-gap: 40px;
  align-items: flex-start;
  padding-bottom: 24px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.06);
}

.gg-rating-row:last-child {
  border-bottom: none;
}

/* Left side: label, score, bar */
.gg-rating-meta {
  font-family: "Sometype Mono", monospace;
  color: var(--gg-brown-dark);
}

.gg-rating-label {
  font-size: 0.9rem;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  margin-bottom: 4px;
}

.gg-rating-score {
  font-size: 0.85rem;
  margin-bottom: 6px;
}

/* Bar styling */
.gg-rating-bar {
  position: relative;
  height: 18px;
  background: transparent;
}

.gg-rating-bar-track {
  position: absolute;
  inset: 0;
  background: var(--gg-cream);
  border: 3px solid var(--gg-brown-dark);
  box-shadow: 3px 3px 0 var(--gg-brown-dark);
}

.gg-rating-bar-fill {
  position: absolute;
  top: 3px;
  left: 3px;
  bottom: 3px;
  background: var(--gg-turquoise);
}

/* 1–5 scores -> width as a fraction of the bar */
.gg-rating-bar-fill.score-1 { width: 20%; }
.gg-rating-bar-fill.score-2 { width: 40%; }
.gg-rating-bar-fill.score-3 { width: 60%; }
.gg-rating-bar-fill.score-4 { width: 80%; }
.gg-rating-bar-fill.score-5 { width: 100%; }

/* Right side: explanation copy */
.gg-rating-copy {
  font-family: "Sometype Mono", monospace;
  font-size: 0.95rem;
  line-height: 1.7;
  color: #6b5a4f;
  max-width: 38rem;
}

/* Mobile: stack meta above copy */
@media (max-width: 800px) {
  .gg-rating-row {
    grid-template-columns: 1fr;
    row-gap: 12px;
  }
}
/* Let the ratings/verdict copy breathe */
.gg-rating-copy {
  max-width: none !important;   /* or bump this to 50rem if you want a limit */
}


/* ============================================================================
   NEO BRUTALIST LANDING PAGE STYLES
   ============================================================================
   Bold, raw, high-contrast styling for course breakdown, vitals, and timeline
   ============================================================================ */

/* ============================================================================
   COURSE BREAKDOWN - SUFFERING ZONES
   ============================================================================ */
.gg-suffering-zones {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 24px;
  margin: 2rem 0;
  padding: 0;
}

.gg-zone-card {
  /* Neo Brutalist: Bold borders, high contrast, raw aesthetic */
  border: 4px solid #000000;
  background: #FFFFFF;
  padding: 0;
  position: relative;
  box-shadow: 8px 8px 0px 0px #000000;
  transition: transform 0.1s ease, box-shadow 0.1s ease;
  overflow: hidden;
}

.gg-zone-card:hover {
  transform: translate(-2px, -2px);
  box-shadow: 10px 10px 0px 0px #000000;
}

/* Consistent alternating backgrounds - ALL cards follow same pattern */
.gg-zone-card:nth-child(odd) {
  background: #F4D03F; /* Muted cream warmth - earth-tone palette, NOT neon yellow */
}

.gg-zone-card:nth-child(even) {
  background: #FFFFFF;
}

.gg-zone-mile {
  /* Bold mile marker - top left corner badge */
  position: absolute;
  top: -12px;
  left: 16px;
  background: #000000;
  color: #FFFFFF;
  font-weight: 900;
  font-size: 14px;
  padding: 6px 12px;
  border: 3px solid #000000;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  z-index: 10;
  box-shadow: 4px 4px 0px 0px #000000;
}

.gg-zone-label {
  /* Header bar - black background, white text */
  font-weight: 900;
  font-size: 20px;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  padding: 24px 20px 16px 20px;
  border-bottom: 4px solid #000000;
  background: #000000;
  color: #FFFFFF;
  line-height: 1.2;
  margin: 0;
}

.gg-zone-desc {
  /* Main description area - consistent padding */
  padding: 20px;
  font-size: 15px;
  line-height: 1.6;
  color: #000000;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

.gg-zone-desc strong {
  /* Named section box - black background, white text, bold border */
  font-weight: 900;
  text-transform: uppercase;
  display: block;
  margin-top: 16px;
  margin-bottom: 12px;
  padding: 10px 14px;
  background: #000000;
  color: #FFFFFF;
  border: 3px solid #000000;
  box-shadow: 4px 4px 0px 0px #000000;
}

.gg-zone-desc em {
  /* Detail boxes - consistent styling with left border */
  font-style: normal;
  display: block;
  margin-top: 10px;
  padding: 10px 14px;
  background: #F0F0F0;
  border-left: 4px solid #000000;
  border-top: 2px solid #000000;
  border-right: 2px solid #000000;
  border-bottom: 2px solid #000000;
  font-weight: 600;
  color: #000000;
}

/* Ensure last detail box has consistent styling */
.gg-zone-desc em:last-of-type {
  margin-bottom: 0;
}

.gg-zone-citation {
  /* Source citation - small, muted but visible */
  padding: 12px 20px;
  background: #F5F5F5;
  border-top: 3px solid #000000;
  font-size: 11px;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: #666666;
}

/* Course Breakdown Note */
.gg-course-breakdown-note {
  /* Research note - raw, honest, muted cream background */
  border: 4px solid #000000;
  background: #F4D03F; /* Muted cream - earth-tone palette */
  padding: 16px 20px;
  margin-top: 2rem;
  font-family: 'Courier New', monospace;
  font-size: 13px;
  line-height: 1.6;
  color: #000000;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  box-shadow: 6px 6px 0px 0px #000000;
}

.gg-course-breakdown-note strong {
  font-weight: 900;
}

/* ============================================================================
   RACE VITALS TABLE - NEO BRUTALIST STYLE
   ============================================================================ */
.gg-vitals-table-wrap {
  margin-top: 2rem;
}

.gg-vitals-table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  border: 4px solid #000000;
  background: #FFFFFF;
  box-shadow: 8px 8px 0px 0px #000000;
}

.gg-vitals-table th {
  /* Label column - black background, white text */
  background: #000000;
  color: #FFFFFF;
  font-weight: 900;
  font-size: 14px;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  padding: 16px 20px;
  text-align: left;
  border-right: 4px solid #000000;
  border-bottom: 2px solid #FFFFFF;
  font-family: 'Courier New', monospace;
}

.gg-vitals-table th:last-child {
  border-right: none;
}

.gg-vitals-table td {
  /* Value column - alternating backgrounds */
  background: #FFFFFF;
  color: #000000;
  font-size: 15px;
  padding: 16px 20px;
  border-bottom: 2px solid #000000;
  border-right: 2px solid #000000;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

.gg-vitals-table tr:nth-child(even) td {
  background: #F4D03F; /* Muted cream warmth - earth-tone palette, NOT neon yellow */
}

.gg-vitals-table tr:last-child th,
.gg-vitals-table tr:last-child td {
  border-bottom: none;
}

.gg-vitals-table td:last-child {
  border-right: none;
}

/* ============================================================================
   TIMELINE SECTION - NEO BRUTALIST STYLE
   ============================================================================ */
.gg-timeline-section {
  position: relative;
  padding-left: 60px;
  margin: 2rem 0;
}

/* Vertical line - bold, high contrast */
.gg-timeline-section::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 6px;
  background: #000000;
  box-shadow: 2px 0 0 0 #000000;
}

.gg-timeline-event {
  position: relative;
  margin-bottom: 32px;
  padding-bottom: 32px;
}

.gg-timeline-event:last-child {
  margin-bottom: 0;
  padding-bottom: 0;
}

/* Diamond marker on timeline */
.gg-timeline-event::before {
  content: '';
  position: absolute;
  left: -48px;
  top: 8px;
  width: 20px;
  height: 20px;
  background: #F4D03F;
  border: 4px solid #000000;
  transform: rotate(45deg);
  box-shadow: 4px 4px 0px 0px #000000;
  z-index: 5;
}

.gg-timeline-year {
  /* Year box - yellow background, black border, bold shadow */
  display: inline-block;
  background: #F4D03F;
  color: #000000;
  font-weight: 900;
  font-size: 16px;
  padding: 10px 16px;
  border: 4px solid #000000;
  box-shadow: 6px 6px 0px 0px #000000;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  font-family: 'Courier New', monospace;
  margin-bottom: 12px;
  position: relative;
  z-index: 2;
}

.gg-timeline-content {
  /* Content text - clean, readable */
  font-size: 15px;
  line-height: 1.7;
  color: #000000;
  padding-left: 0;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

/* ============================================================================
   FACTS SECTION
   ============================================================================ */
.gg-facts-header {
  /* Dynamic facts header - bold, brutal */
  font-weight: 900;
  font-size: 32px;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  margin: 2rem 0 1.5rem 0;
  padding: 16px 24px;
  background: #000000;
  color: #FFFFFF;
  border: 4px solid #000000;
  display: inline-block;
  box-shadow: 8px 8px 0px 0px #F4D03F;
  line-height: 1.1;
}

.gg-facts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  margin: 1.5rem 0;
}

.gg-fact-card {
  /* Neo brutalist fact cards */
  border: 4px solid #000000;
  background: #FFFFFF;
  padding: 0;
  position: relative;
  box-shadow: 6px 6px 0px 0px #000000;
  transition: transform 0.1s ease, box-shadow 0.1s ease;
}

.gg-fact-card:hover {
  transform: translate(-2px, -2px);
  box-shadow: 8px 8px 0px 0px #000000;
}

.gg-fact-card:nth-child(odd) {
  background: #FFFFFF;
}

.gg-fact-card:nth-child(even) {
  background: #F0F0F0;
}

.gg-fact-number {
  /* Bold number badge */
  position: absolute;
  top: -16px;
  left: 20px;
  background: #000000;
  color: #FFFFFF;
  font-weight: 900;
  font-size: 24px;
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 4px solid #000000;
  box-shadow: 4px 4px 0px 0px #F4D03F;
  z-index: 10;
}

.gg-fact-text {
  /* Fact content */
  padding: 32px 20px 20px 20px;
  font-size: 14px;
  line-height: 1.7;
  color: #000000;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

/* ============================================================================
   RESPONSIVE ADJUSTMENTS
   ============================================================================ */
@media (max-width: 768px) {
  .gg-suffering-zones {
    grid-template-columns: 1fr;
    gap: 20px;
  }

  .gg-facts-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .gg-zone-card,
  .gg-fact-card {
    box-shadow: 4px 4px 0px 0px #000000;
  }

  .gg-zone-card:hover,
  .gg-fact-card:hover {
    transform: none;
    box-shadow: 4px 4px 0px 0px #000000;
  }

  .gg-facts-header {
    font-size: 24px;
    padding: 12px 16px;
    box-shadow: 4px 4px 0px 0px #F4D03F;
  }

  .gg-timeline-section {
    padding-left: 40px;
  }

  .gg-timeline-event::before {
    left: -32px;
    width: 16px;
    height: 16px;
  }

  .gg-vitals-table {
    font-size: 13px;
  }

  .gg-vitals-table th,
  .gg-vitals-table td {
    padding: 12px 16px;
  }
}

/* ============================================================================
   HIGH CONTRAST MODE SUPPORT
   ============================================================================ */
@media (prefers-contrast: high) {
  .gg-zone-card,
  .gg-fact-card {
    border-width: 5px;
  }

  .gg-zone-mile,
  .gg-fact-number {
    border-width: 4px;
  }

  .gg-vitals-table {
    border-width: 5px;
  }

  .gg-timeline-year {
    border-width: 5px;
  }
}
